// Aggregate database health monitoring results
const connectionTest = $node['Test Database Connection'].json;
const coreTables = $node['Check Core Tables Structure'].json;
const performanceStats = $node['Check Database Performance Stats'].json;
const dataIntegrity = $node['Check Data Integrity'].json;
const rlsPolicies = $node['Check RLS Policies'].json;
const healthAPI = $node['Test Health API'].json;
const databaseAPI = $node['Test Database-Dependent API'].json;

const results = {
  timestamp: new Date().toISOString(),
  databaseConnection: {
    status: connectionTest?.connection_test === 1 ? 'HEALTHY' : 'FAILED',
    postgresVersion: connectionTest?.postgres_version || 'N/A',
    dbTimestamp: connectionTest?.db_timestamp || 'N/A'
  },
  coreTablesHealth: {
    status: Array.isArray(coreTables) && coreTables.length >= 5 ? 'HEALTHY' : 'MISSING_TABLES',
    tablesFound: Array.isArray(coreTables) ? coreTables.length : 0,
    expectedTables: 5,
    details: Array.isArray(coreTables) ? coreTables.map(t => t.tablename).join(', ') : 'N/A'
  },
  performanceStats: {
    status: Array.isArray(performanceStats) && performanceStats.length > 0 ? 'AVAILABLE' : 'NO_STATS',
    largestTable: Array.isArray(performanceStats) && performanceStats.length > 0 ? 
      performanceStats[0]?.tablename + ' (' + performanceStats[0]?.total_size + ')' : 'N/A',
    totalTables: Array.isArray(performanceStats) ? performanceStats.length : 0
  },
  dataIntegrityCheck: {
    status: 'ANALYZED',
    issues: [],
    summary: Array.isArray(dataIntegrity) ? dataIntegrity.map(row => 
      `${row.table_name}: ${row.total_records} records`
    ).join(', ') : 'N/A'
  },
  rlsPolicies: {
    status: Array.isArray(rlsPolicies) && rlsPolicies.length > 0 ? 'ACTIVE' : 'NO_POLICIES',
    policyCount: Array.isArray(rlsPolicies) ? rlsPolicies.length : 0,
    tables: Array.isArray(rlsPolicies) ? 
      [...new Set(rlsPolicies.map(p => p.tablename))].join(', ') : 'N/A'
  },
  apiConnectivity: {
    healthAPI: healthAPI?.statusCode === 200 ? 'HEALTHY' : 'FAILED',
    databaseAPI: databaseAPI?.statusCode === 200 ? 'HEALTHY' : 'FAILED',
    healthAPICode: healthAPI?.statusCode || 'ERROR',
    databaseAPICode: databaseAPI?.statusCode || 'ERROR'
  }
};

// Check for data integrity issues
if (Array.isArray(dataIntegrity)) {
  dataIntegrity.forEach(row => {
    if (row.missing_created_at > 0) {
      results.dataIntegrityCheck.issues.push(`${row.table_name}: ${row.missing_created_at} missing created_at`);
    }
    if (row.missing_title > 0) {
      results.dataIntegrityCheck.issues.push(`${row.table_name}: ${row.missing_title} missing titles`);
    }
    if (row.missing_email > 0) {
      results.dataIntegrityCheck.issues.push(`${row.table_name}: ${row.missing_email} missing emails`);
    }
    if (row.invalid_status > 0) {
      results.dataIntegrityCheck.issues.push(`${row.table_name}: ${row.invalid_status} invalid statuses`);
    }
    if (row.invalid_role > 0) {
      results.dataIntegrityCheck.issues.push(`${row.table_name}: ${row.invalid_role} invalid roles`);
    }
  });
}

results.dataIntegrityCheck.status = results.dataIntegrityCheck.issues.length === 0 ? 'CLEAN' : 'ISSUES_FOUND';

// Calculate overall database health
const criticalIssues = [
  results.databaseConnection.status !== 'HEALTHY',
  results.coreTablesHealth.status !== 'HEALTHY',
  results.apiConnectivity.healthAPI !== 'HEALTHY',
  results.dataIntegrityCheck.status === 'ISSUES_FOUND'
];

results.overallHealth = criticalIssues.some(issue => issue) ? 'DEGRADED' : 'HEALTHY';
results.criticalIssueCount = criticalIssues.filter(issue => issue).length;

return results;
